import {useState} from 'react'
import TodoCreate from './components/TodoCreate'
import TodoList from './components/TodoList'
import './styles.css'

const App = () => {
    if (
      document.getElementByClass('update-form').style.visibility == 'visible'
    ) {
      document.getElementByClass('button-container').style.visibility = 'hidden'
    }
    
  const [todos, setTodos] = useState([])

  const createTodo = (title) => {
    console.log('new todo with ', title)
    const updatedTodos = [
      ...todos,
      {id: Math.round(Math.random() * 9999999), title},
    ]
    setTodos(updatedTodos)
  }

  const deleteTodoById = (id) => {
    const updatedTodos = todos.filter((todo) => {
      return todo.id !== id
    })

    setTodos(updatedTodos)
  }

  const editTodoById = (id, newTitle) => {
    const updatedTodos = todos.map((todo) => {
        return {...todo, title: newTitle}
      

      return todo
    })
    setTodos(updatedTodos)
  }

  return (
    <div>
      <TodoCreate onCreate={createTodo} />
      <TodoList todos={todos} onDelete={deleteTodoById} onEdit={editTodoById} />
    </div>
  )
}

export default App
